import React, { useEffect, useState } from 'react';
import { fakeApi } from '../api/fakeApi';

export default function Transactions(){
  const [txns, setTxns] = useState([]);
  const [form, setForm] = useState({ title:'', amount:0, type:'expense', category:'General', date:new Date().toISOString().slice(0,10) });

  useEffect(()=>{ load(); },[]);
  const load = async ()=>{
    const list = await fakeApi.listTransactions();
    setTxns(list);
  };
  const add = async (e)=>{
    e.preventDefault();
    const t = { ...form, amount:Number(form.amount) };
    await fakeApi.addTransaction(t);
    setForm({ title:'', amount:0, type:'expense', category:'General', date:new Date().toISOString().slice(0,10) });
    load();
  };
  const remove = async (id)=>{ await fakeApi.deleteTransaction(id); load(); };

  return (
    <div>
      <div className="page-title">Transactions</div>
      <div className="card" style={{marginBottom:16}}>
        <form onSubmit={add} style={{display:'grid', gridTemplateColumns:'1fr 140px 120px 140px 90px', gap:8}}>
          <input placeholder="Title" value={form.title} onChange={e=>setForm({...form, title:e.target.value})} required />
          <input type="number" placeholder="Amount" value={form.amount} onChange={e=>setForm({...form, amount:e.target.value})} required />
          <select value={form.type} onChange={e=>setForm({...form, type:e.target.value})}>
            <option value="expense">Expense</option>
            <option value="income">Income</option>
          </select>
          <input type="date" value={form.date} onChange={e=>setForm({...form, date:e.target.value})} />
          <button className="btn" type="submit">Add</button>
        </form>
      </div>

      <div className="card">
        <h3>All Transactions</h3>
        <div className="transactions-list">
          {txns.length===0 && <div style={{color:'#6b7280'}}>No transactions found</div>}
          {txns.map(t=> (
            <div key={t.id} className="txn">
              <div>
                <div style={{fontWeight:700}}>{t.title}</div>
                <div style={{fontSize:12, color:'#6b7280'}}>{t.category} • {new Date(t.date).toLocaleDateString()}</div>
              </div>
              <div style={{display:'flex', gap:8, alignItems:'center'}}>
                <div style={{fontWeight:700, color: t.type==='income'? 'green':'red'}}>{t.type==='income'?'+':'-'}₹{t.amount.toFixed(2)}</div>
                <button onClick={()=>remove(t.id)} style={{border:'none', background:'transparent', color:'#ef4444', cursor:'pointer'}}>Delete</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
