import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { fakeApi } from '../api/fakeApi';

export default function Profile(){
  const { user, logout } = useAuth();
  const [profile, setProfile] = useState(null);
  const [bio, setBio] = useState('');
  useEffect(()=>{ if(user) load(); },[user]);
  const load = async ()=>{
    const p = await fakeApi.getProfile(user.email);
    if(!p.error) setProfile(p);
  };
  const save = async ()=>{
    await fakeApi.updateProfile(user.email, { username: profile.username, bio });
    load();
  };
  if(!profile) return <div>Loading...</div>;
  return (
    <div>
      <div className="page-title">Profile</div>
      <div className="card" style={{maxWidth:800}}>
        <div style={{display:'grid', gridTemplateColumns:'1fr 2fr', gap:20}}>
          <div>
            <div style={{width:120,height:120,borderRadius:12, background:'linear-gradient(180deg,#7c3aed,#60a5fa)', color:'#fff', display:'flex', alignItems:'center', justifyContent:'center', fontSize:40}}>{profile.username[0].toUpperCase()}</div>
            <div style={{marginTop:12, fontWeight:700}}>{profile.username}</div>
            <div style={{color:'#6b7280'}}>{profile.email}</div>
          </div>
          <div>
            <div className="form-row">
              <label>Full name</label>
              <input value={profile.username} onChange={e=>setProfile({...profile, username:e.target.value})} />
            </div>
            <div className="form-row">
              <label>Bio</label>
              <textarea value={bio} onChange={e=>setBio(e.target.value)} rows={4} />
            </div>
            <div style={{display:'flex', gap:8}}>
              <button onClick={save} className="btn">Save Changes</button>
              <button onClick={logout} className="btn secondary">Logout</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
