import React, { useEffect, useState } from 'react';
import { fakeApi } from '../api/fakeApi';

export default function Budgets(){
  const [list, setList] = useState([]);
  const [form, setForm] = useState({ name:'', limit:0 });
  useEffect(()=>{ load(); },[]);
  const load = async ()=>{ const l = await fakeApi.listBudgets(); setList(l); };
  const add = async (e)=>{ e.preventDefault(); await fakeApi.addBudget({ name:form.name, limit:Number(form.limit) }); setForm({name:'', limit:0}); load(); };
  return (
    <div>
      <div className="page-title">Budgets</div>
      <div className="card" style={{marginBottom:16}}>
        <form onSubmit={add} style={{display:'flex', gap:8}}>
          <input placeholder="Category" value={form.name} onChange={e=>setForm({...form, name:e.target.value})} required />
          <input type="number" placeholder="Limit" value={form.limit} onChange={e=>setForm({...form, limit:e.target.value})} required />
          <button className="btn" type="submit">Create</button>
        </form>
      </div>
      <div className="card">
        <h3>Your Budgets</h3>
        {list.length===0 && <div style={{color:'#6b7280'}}>No budgets yet</div>}
        <div style={{display:'grid', gap:12}}>
          {list.map(b=> (
            <div key={b.id} className="txn">
              <div>
                <div style={{fontWeight:700}}>{b.name}</div>
                <div style={{fontSize:12, color:'#6b7280'}}>Spent: ₹{(b.spent||0).toFixed(2)} / Limit: ₹{(b.limit||0).toFixed(2)}</div>
              </div>
              <div style={{color: '#2563eb', fontWeight:700}}> {((b.spent||0)/(b.limit||1)*100).toFixed(1)}% </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
